import React, { useState } from 'react';
import './Signup.css';

const SignUp = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phoneNumber: '',
    session: '',
    comments: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSessionChange = (e) => {
    setFormData(prev => ({
      ...prev,
      session: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    alert('Thank you for registering for Bible Study!');
    // Here you would typically send the data to a backend
  };

  return (
    <div className="signup-container">
      <h1 className='signup-title'>Fl<i className="fas fa-fire fa-rotate-45" style={{ margin: '0 6px' }}></i>re Bible Study Registration Form</h1>
      <p className='signup-description'>Please fill out the form below to register for our Bible study sessions</p>
      
      <form onSubmit={handleSubmit}>
        <div className="form-section">
          <h2>Full Name</h2>
          <div className="name-fields">
            <input
              type="text"
              name="firstName"
              placeholder="First Name"
              value={formData.firstName}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="lastName"
              placeholder="Last Name"
              value={formData.lastName}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        <div className="form-section">
          <h2>Email</h2>
          <input
            type="email"
            name="email"
            placeholder="example@example.com"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-section">
          <h2>Phone Number</h2>
          <input
            type="tel"
            name="phoneNumber"
            placeholder="(123) 456-7890"
            value={formData.phoneNumber}
            onChange={handleChange}
            required
          />
          <p className="hint">Please enter a valid phone number.</p>
        </div>

        <div className="form-footer">
          <button type="submit" className="submit-btn">
            Submit
          </button>
          <div className="already-registered">
            Already registered? <a href="/signin">Sign in</a>
          </div>
        </div>
      </form>
    </div>
  );
};

export default SignUp;
