import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Sidebar.css';

const sidebarLinks = [
  {
    header: 'Version 2.0 Demo',
    items: [
      {
        icon: 'fas fa-scroll',
        label: 'Flare',
        to: '/',
        subLinks: [
          { label: 'Flare', to: '/' },
          { label: 'HisPattern.com', to: 'https://Hispattern.com', external: true },
        ],
      },
    ],
  },
  {
    header: 'Version 1.0 Subject Maps',
    items: [
      {
        icon: 'fas fa-cross',
        label: 'Jesus',
        to: '#',
        subLinks: [
          { label: 'Old Testament Jesus 1', to: '/old-testament-jesus-1' },
          { label: 'Old Testament Jesus 2', to: '/old-testament-jesus-2' },
        ],
      },
      {
        icon: 'fas fa-heart',
        label: 'Promises',
        to: '#',
        subLinks: [
          { label: 'Messages of Hope', to: '/messages-of-hope' },
        ],
      },
      {
        icon: 'fas fa-clock',
        label: 'Prophecy',
        to: '#',
        subLinks: [
          { label: 'Empires', to: '/world-empires' },
          { label: 'Keys (todo)', to: '/prophecy-keys', disabled: true },
        ],
      },
      {
        icon: 'fas fa-leaf',
        label: 'Land of the Living',
        to: '#',
        subLinks: [
          { label: 'What Is Spirit', to: '/what-is-spirit' },
        ],
      },
    ],
  },
];

const Sidebar = ({ collapsed }) => {
  const location = useLocation();
  return (
    <div className="main-sidebar sidebar-style-2 fixed-sidebar">
      <aside id="sidebar-wrapper">
        <div className="sidebar-brand">
          <Link to="/">
            <i className="mdi mdi-fire"></i>
            <span className="sidebar-label">Flare BIBLE STUDY</span>
          </Link>
        </div>
        <div className="sidebar-brand sidebar-brand-sm">
          <Link to="/">
            <h1><i className="fas fa-fire fa-rotate-45"></i></h1>
          </Link>
        </div>
        <ul className="sidebar-menu">
          {sidebarLinks.map((section, idx) => (
            <React.Fragment key={idx}>
              <li className="menu-header">{section.header}</li>
              {section.items.map((item, i) => (
                <li className="nav-item dropdown sidebar-icon-link" key={i}>
                  <div className="nav-link has-dropdown sidebar-icon-wrapper">
                    <i className={item.icon}></i>
                    <span className="sidebar-label">{item.label}</span>
                  </div>
                  <ul className="dropdown-menu">
                    {item.subLinks && item.subLinks.map((sub, j) => (
                      sub.external ? (
                        <li key={j}><a className="nav-link" href={sub.to} target="_blank" rel="noopener noreferrer">{sub.label}</a></li>
                      ) : (
                        <li key={j}>
                          <Link className={`nav-link${location.pathname === sub.to ? ' active' : ''}${sub.disabled ? ' disabled' : ''}`} to={sub.to} tabIndex={sub.disabled ? -1 : 0} aria-disabled={sub.disabled}>{sub.label}</Link>
                        </li>
                      )
                    ))}
                  </ul>
                </li>
              ))}
            </React.Fragment>
          ))}
        </ul>
      </aside>
    </div>
  );
};

export default Sidebar;
