import React, { useState } from 'react';
import './Signup.css';

const Signup: React.FC = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phoneNumber: '',
    session: '',
    comments: '',
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSessionChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData(prev => ({
      ...prev,
      session: e.target.value,
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    alert('Thank you for registering for Bible Study!');
    // Here you would typically send the data to a backend
  };

  return (
    <div className="signup-container">
      <h1>Bible Study Registration Form</h1>
      <p>Please fill out the form below to register for our Bible study sessions</p>
      
      <form onSubmit={handleSubmit}>
        {/* Previous form sections remain the same */}

        <div className="form-section">
          <h2>Additional Comments</h2>
          <textarea
            name="comments"
            value={formData.comments}
            onChange={handleChange}
          />
        </div>

        <div className="form-footer">
          <button type="submit" className="submit-btn">
            Submit
          </button>
          <div className="already-registered">
            Already registered? <a href="/signin">Sign in</a>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Signup;
