import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import HomePage from './HomePage';
import Footer from './components/Footer';

function App() {
  const [sidebarCollapsed, setSidebarCollapsed] = React.useState(false);

  const toggleSidebar = () => {
    setSidebarCollapsed(!sidebarCollapsed);
  };

  return (
    <Router>
      <div id="app">
        <div className={`main-wrapper main-wrapper-1 ${sidebarCollapsed ? 'sidebar-mini' : ''}`}>
          <div className="navbar-bg"></div>
          <Navbar onToggleSidebar={toggleSidebar} />
          <Sidebar collapsed={sidebarCollapsed} />
          {/* Main Content */}
          <div className="main-content">
            <Routes>
              <Route path="/" element={<HomePage />} />
              {/* Future routes can be added here, e.g.:
              <Route path="/old-testament-jesus-1" element={<OldTestamentJesus1 />} />
              <Route path="/messages-of-hope" element={<MessagesOfHope />} />
              */}
            </Routes>
          </div>
          <Footer />
        </div>
      </div>
    </Router>
  );
}

export default App;
